import React, { useState, useEffect } from 'react';
import { useReports } from '../context/ReportContext';
import { useEvents } from '../hooks/useEvents';
import { useNotifications } from '../hooks/useNotifications';

const CleaningParties = () => {
  const { verifiedStatuses } = useReports();
  const { events, loading, createEvent, joinEvent, leaveEvent } = useEvents();
  const [username, setUsername] = useState('');
  const [userId, setUserId] = useState('');
  const [showUsernameModal, setShowUsernameModal] = useState(true);
  const [activeTab, setActiveTab] = useState('events');
  const [newEvent, setNewEvent] = useState({
    title: '',
    location: '',
    date: '',
    time: '',
    description: '',
    maxParticipants: 20,
    organizer: '',
    contactInfo: ''
  });
  const [showEventForm, setShowEventForm] = useState(false);
  const [filterStatus, setFilterStatus] = useState('all'); // all, upcoming, past

  // Generate unique user ID
  useEffect(() => {
    if (username && !userId) {
      const id = `user_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
      setUserId(id);
    }
  }, [username, userId]);

  const handleCreateEvent = async (e) => {
    e.preventDefault();
    try {
      const eventData = {
        ...newEvent,
        organizer: username,
        organizerId: userId,
        attendees: [{ userId, userName: username, joinedAt: new Date() }],
        status: 'upcoming'
      };

      await createEvent(eventData);
      
      setNewEvent({
        title: '',
        location: '',
        date: '',
        time: '',
        description: '',
        maxParticipants: 20,
        organizer: '',
        contactInfo: ''
      });
      setShowEventForm(false);
    } catch (error) {
      console.error('Error creating event:', error);
    }
  };

  const handleJoinEvent = async (eventId) => {
    try {
      await joinEvent(eventId, userId, username);
    } catch (error) {
      console.error('Error joining event:', error);
    }
  };

  const handleLeaveEvent = async (eventId) => {
    try {
      await leaveEvent(eventId, userId);
    } catch (error) {
      console.error('Error leaving event:', error);
    }
  };

  const formatTime = (date) => {
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  const isUserAttending = (event) => {
    return event.attendees?.some(attendee => attendee.userId === userId);
  };

  const isEventFull = (event) => {
    return event.attendees?.length >= event.maxParticipants;
  };

  const isEventPast = (event) => {
    const eventDateTime = new Date(`${event.date}T${event.time}`);
    return eventDateTime < new Date();
  };

  const filteredEvents = events.filter(event => {
    if (filterStatus === 'upcoming') return !isEventPast(event);
    if (filterStatus === 'past') return isEventPast(event);
    return true;
  });

  if (showUsernameModal) {
    return (
      <div className="username-modal">
        <div className="username-modal-content">
          <h2>🌊 Welcome to Cleaning Parties!</h2>
          <p>Enter your name to join the community chat and organize cleaning events</p>
          <form onSubmit={(e) => {
            e.preventDefault();
            if (username.trim()) {
              setShowUsernameModal(false);
            }
          }}>
            <input
              type="text"
              placeholder="Enter your name"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="username-input"
              required
            />
            <button type="submit" className="btn btn-primary">
              Join Community
            </button>
          </form>
        </div>
      </div>
    );
  }

  return (
    <section className="cleaning-parties-section">
      <div className="container">
        <div className="parties-header">
          <h1>🤝 Community Cleaning Parties</h1>
          <p>Connect with fellow water advocates and organize cleaning events</p>
        </div>

        <div className="parties-tabs">
          <button 
            className={`tab-button ${activeTab === 'chat' ? 'active' : ''}`}
            onClick={() => setActiveTab('chat')}
          >
            <i className="fas fa-comments"></i>
            <span>Community Chat</span>
          </button>
          <button 
            className={`tab-button ${activeTab === 'events' ? 'active' : ''}`}
            onClick={() => setActiveTab('events')}
          >
            <i className="fas fa-calendar-alt"></i>
            <span>Upcoming Events</span>
          </button>
        </div>

        {activeTab === 'chat' && (
          <div className="chat-container">
            <div className="chat-header">
              <h3>💬 Live Community Chat</h3>
              <div className="online-indicator">
                <div className="online-dot"></div>
                <span>Online</span>
              </div>
            </div>

            <div className="chat-messages">
              {messages.map(message => (
                <div key={message.id} className={`message ${message.type}`}>
                  <div className="message-header">
                    <span className="message-username">{message.username}</span>
                    <span className="message-time">{formatTime(message.timestamp)}</span>
                  </div>
                  <div className="message-content">{message.message}</div>
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>

            <form className="chat-input-form" onSubmit={handleSendMessage}>
              <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Type your message..."
                className="chat-input"
              />
              <button type="submit" className="send-button">
                <i className="fas fa-paper-plane"></i>
              </button>
            </form>
          </div>
        )}

        {activeTab === 'events' && (
          <div className="events-container">
            <div className="events-header">
              <h3>📅 Upcoming Cleaning Events</h3>
              <button 
                className="btn btn-primary"
                onClick={() => setShowEventForm(true)}
              >
                <i className="fas fa-plus"></i>
                Create Event
              </button>
            </div>

            {showEventForm && (
              <div className="event-form-modal">
                <div className="event-form-content">
                  <h3>Create New Cleaning Event</h3>
                  <form onSubmit={handleCreateEvent}>
                    <div className="form-group">
                      <label>Event Title</label>
                      <input
                        type="text"
                        value={newEvent.title}
                        onChange={(e) => setNewEvent({...newEvent, title: e.target.value})}
                        placeholder="e.g., Lake Anna Cleanup"
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label>Location</label>
                      <select
                        value={newEvent.location}
                        onChange={(e) => setNewEvent({...newEvent, location: e.target.value})}
                        required
                      >
                        <option value="">Select a water body</option>
                        {Object.keys(verifiedStatuses).map(location => (
                          <option key={location} value={location}>{location}</option>
                        ))}
                      </select>
                    </div>
                    <div className="form-row">
                      <div className="form-group">
                        <label>Date</label>
                        <input
                          type="date"
                          value={newEvent.date}
                          onChange={(e) => setNewEvent({...newEvent, date: e.target.value})}
                          required
                        />
                      </div>
                      <div className="form-group">
                        <label>Time</label>
                        <input
                          type="time"
                          value={newEvent.time}
                          onChange={(e) => setNewEvent({...newEvent, time: e.target.value})}
                          required
                        />
                      </div>
                    </div>
                    <div className="form-group">
                      <label>Description</label>
                      <textarea
                        value={newEvent.description}
                        onChange={(e) => setNewEvent({...newEvent, description: e.target.value})}
                        placeholder="Describe the cleanup activities and what to bring..."
                        rows="3"
                      />
                    </div>
                    <div className="form-group">
                      <label>Max Participants</label>
                      <input
                        type="number"
                        value={newEvent.maxParticipants}
                        onChange={(e) => setNewEvent({...newEvent, maxParticipants: parseInt(e.target.value)})}
                        min="1"
                        max="100"
                      />
                    </div>
                    <div className="form-actions">
                      <button type="button" onClick={() => setShowEventForm(false)} className="btn btn-secondary">
                        Cancel
                      </button>
                      <button type="submit" className="btn btn-primary">
                        Create Event
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            )}

            <div className="events-list">
              {events.length === 0 ? (
                <div className="no-events">
                  <i className="fas fa-calendar-plus"></i>
                  <h4>No events yet</h4>
                  <p>Be the first to create a cleaning event!</p>
                </div>
              ) : (
                events.map(event => (
                  <div key={event.id} className="event-card">
                    <div className="event-header">
                      <h4>{event.title}</h4>
                      <div className="event-status">
                        <span className="participants-count">
                          {event.participants.length}/{event.maxParticipants} participants
                        </span>
                      </div>
                    </div>
                    <div className="event-details">
                      <div className="event-info">
                        <i className="fas fa-map-marker-alt"></i>
                        <span>{event.location}</span>
                      </div>
                      <div className="event-info">
                        <i className="fas fa-calendar"></i>
                        <span>{formatDate(event.date)}</span>
                      </div>
                      <div className="event-info">
                        <i className="fas fa-clock"></i>
                        <span>{event.time}</span>
                      </div>
                    </div>
                    {event.description && (
                      <p className="event-description">{event.description}</p>
                    )}
                    <div className="event-actions">
                      <button 
                        className="btn btn-primary"
                        onClick={() => joinEvent(event.id)}
                        disabled={event.participants.includes(username) || event.participants.length >= event.maxParticipants}
                      >
                        {event.participants.includes(username) ? 'Joined' : 'Join Event'}
                      </button>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default CleaningParties;
