import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ReportProvider } from './context/ReportContext';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import WaterMap from './components/WaterMap';
import Dashboard from './components/Dashboard';
import ReportForm from './components/ReportForm';
import About from './components/About';
import Footer from './components/Footer';
import Notification from './components/Notification';
import LocationAlert from './components/LocationAlert';
import SearchAndDirections from './components/SearchAndDirections';
import './App.css';

function App() {
  const [notifications, setNotifications] = useState([]);

  const addNotification = (message, type = 'info') => {
    const id = Date.now();
    const notification = { id, message, type };
    setNotifications(prev => [...prev, notification]);
    
    // Auto remove after 5 seconds
    setTimeout(() => {
      removeNotification(id);
    }, 5000);
  };

  const removeNotification = (id) => {
    setNotifications(prev => prev.filter(notification => notification.id !== id));
  };

  return (
    <ReportProvider>
      <Router>
        <div className="App">
          <Navbar />
          <main>
            <Routes>
              <Route path="/" element={
                <>
                  <Hero />
                  <WaterMap />
                  <Dashboard />
                  <ReportForm onReportSubmit={addNotification} />
                  <About />
                </>
              } />
              <Route path="/map" element={<WaterMap />} />
              <Route path="/dashboard" element={<Dashboard />} />
              <Route path="/report" element={<ReportForm onReportSubmit={addNotification} />} />
              <Route path="/about" element={<About />} />
            </Routes>
          </main>
          <Footer />
          
          {/* Notification Container */}
          <div className="notification-container">
            {notifications.map(notification => (
              <Notification
                key={notification.id}
                message={notification.message}
                type={notification.type}
                onClose={() => removeNotification(notification.id)}
              />
            ))}
          </div>
        </div>
      </Router>
    </ReportProvider>
  );
}

export default App;
